$(function () {
    if (Modernizr.touch) {
        var e = $(".focus-highlight");
        e.length && e.find("td, th").attr("tabindex", "1").on("touchstart", function () {
            $(this).focus()
        }), $(".tablesorter").find("th").addClass("needsclick")
    }
   altair_tablesorter.custom_filters()
}), altair_tablesorter = {
    custom_filters: function () {
        $("#dt_tableExportDistricts_wrapper").tablesorter({
            theme: "altair",
            headerTemplate: "{content} {icon}",
            widgets: ["zebra", "filter"],
            widgetOptions: {
                filter_reset: "button.ts_cf_reset",
                filter_cssFilter: ["", "ts_cf_range", "ts_cf_select_single", "ts_cf_datepicker"]
            }
        });
    }
};